const encoded = '#1lfA9IHTuP3iVP3eyKDIpb2mvPVU2lkITNg00b2X#';

function decode(x) {
    if (x.substr(0, 2) == "#1") {
        let s = x.substr(2);
        // pepper logic: replace # with +
        // s = s.replace(/\+/g, "#"); // + becomes #
        // s = s.replace(/#/g, "+");  // # becomes +
        // effectively # -> + and + -> +
        s = s.replace(/#/g, "+");

        return Buffer.from(s, 'base64').toString('utf8');
    }
    return x;
}

console.log(decode(encoded));
